import React from 'react';
import Home from "./Pages/Home";
import LandingPage from "./Pages/LandingPage";
import NavBar from "./Components/NavBar";
import {BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import ChatBot from 'react-simple-chatbot';

const steps = [
    {
      id: '1',
      message: 'Custom components',
      trigger: '2',
    },
    {
      id: '2',
      component: (
        <div style={{ width: '100%' }}>
          <h3>Summary</h3>
          <table>
            <tbody>
              <tr>
                <td>Name</td>
                <td>{name.value}</td>
              </tr>
              <tr>
                <td>Gender</td>
                <td>{gender.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{age.value}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ),
      end: true,
    },
  ];

export default function App(){
    return(
      <Router>
        <NavBar />
        <Switch>
          <Route exact path="/">
            <LandingPage />
          </Route>
          <Route path="/home">
            <Home />
          </Route>
        </Switch>
        <ChatBot steps={steps} />
      </Router>
    );
}
